import React, { Component } from 'react';
import {server} from '../config';

class Persons extends Component{

    state = {
        personsList: [
            {
                id: 1,
                firstName: "Cool",
                surName: "Cat",
                ssn: 9009161358,
                country: "Sweden",
                city: "Stockholm",
            },
            {
                id: 2,
                firstName: "Klas",
                surName: "Banan",
                ssn: 9908161756,
                country: "UK",
                city: "London",
            }

        ],
    }


    constructor(props) {
        super(props);
        //this.fetchPersons();
    }

    fetchPersons(){
        fetch(server + "/getPersons")
            .then((response) => {
                return response.json();
            })
            .then((response) =>
            {
                if ((response.error) && response.status === 403) {

                }
                else if (response.error) throw new Error("Something went wrong. Please try again.");
                else return response;
            })
            .then(data =>{
                this.updatePersonsList(data);})
            .catch(e => {
                alert(e.message);
            })
    }

    updatePersonsList(data){
        this.setState({personsList: data})
    }

    showPersonsList() {
        if(this.state.personsList && this.state.personsList.length > 0){
            return <div>
                { this.state.personsList.map((person, index) =>
                    <div id={"listDiv"} key={"l" + person.id}>

                        <p id={"listID"}>{"ID: " + person.id}</p>

                        <p id={"listFirst"}>{"First name: " + person.firstName}</p>

                        <p id={"listSur"}>{"Surname: " + person.surName}</p>

                        <p id={"listSSN"}>{"SSN: " + person.ssn}</p>

                        <p id={"listCountry"}>{"Country: " + person.country}</p>

                        <p id={"listCity"}>{"City: " + person.city}</p>
                    </div>)
                }
            </div>;
        }
        else{
            return <div id={"listView"}>no results</div>
        }
    }


    render(){
        return(
            <div>
                {this.showPersonsList()}
            </div>
        )
    }

}

export default Persons;
